mapper.select:
        SELECT
        

--         级联表的字段

        FROM zuowei  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.zuowei_name like CONCAT('%',#{params.zuoweiName},'%')
            
            
                and a.zuowei_types = #{params.zuoweiTypes}
            
            
                and a.zuowei_shijian like CONCAT('%',#{params.zuoweiShijian},'%')
            
            
                  and a.zuowei_new_money >= #{params.zuoweiNewMoneyStart}   
            
            
                  and a.zuowei_new_money <= #{params.zuoweiNewMoneyEnd}   
            
            
                  and a.zuowei_number >= #{params.zuoweiNumberStart}   
            
            
                  and a.zuowei_number <= #{params.zuoweiNumberEnd}   
            
             
                and a.zuowei_number = #{params.zuoweiNumber}
             
            
                  and a.zuowei_clicknum >= #{params.zuoweiClicknumStart}   
            
            
                  and a.zuowei_clicknum <= #{params.zuoweiClicknumEnd}   
            
             
                and a.zuowei_clicknum = #{params.zuoweiClicknum}
             
            
                  and a.zuowei_delete >= #{params.zuoweiDeleteStart}   
            
            
                  and a.zuowei_delete <= #{params.zuoweiDeleteEnd}   
            
             
                and a.zuowei_delete = #{params.zuoweiDelete}
             
            
                and a.zuowei_content like CONCAT('%',#{params.zuoweiContent},'%')
            

        

        order by a.${params.orderBy} desc
