mapper.select:
        SELECT
        

--         级联表的字段
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_delete as yonghuDelete
        ,zuowei.zuowei_name as zuoweiName
        ,zuowei.zuowei_photo as zuoweiPhoto
        ,zuowei.zuowei_types as zuoweiTypes
        ,zuowei.zuowei_shijian as zuoweiShijian
        ,zuowei.zuowei_new_money as zuoweiNewMoney
        ,zuowei.zuowei_number as zuoweiNumber
        ,zuowei.zuowei_clicknum as zuoweiClicknum
        ,zuowei.zuowei_delete as zuoweiDelete
        ,zuowei.zuowei_content as zuoweiContent

        FROM zuowei_order  a
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
        left JOIN zuowei zuowei ON a.zuowei_id = zuowei.id

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.zuowei_order_uuid_number like CONCAT('%',#{params.zuoweiOrderUuidNumber},'%')
            
            
                and (
                    a.zuowei_id = #{params.zuoweiId}
                )
            
            
                and (
                    a.yonghu_id = #{params.yonghuId}
                )
            
            
                  and a.zuowei_order_true_price >= #{params.zuoweiOrderTruePriceStart}   
            
            
                  and a.zuowei_order_true_price <= #{params.zuoweiOrderTruePriceEnd}   
            
            
                and a.zuowei_order_types = #{params.zuoweiOrderTypes}
            
            
                and a.zuowei_order_payment_types = #{params.zuoweiOrderPaymentTypes}
            
            
                and a.buy_zuowei_number like CONCAT('%',#{params.buyZuoweiNumber},'%')
            
            
                  and UNIX_TIMESTAMP(a.buy_zuowei_time) >= UNIX_TIMESTAMP(#{params.buyZuoweiTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.buy_zuowei_time) <= UNIX_TIMESTAMP(#{params.buyZuoweiTimeEnd}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            

                
            
                and a.yonghu_id IS NOT NULL
            
            
                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
            
            
                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
            
            
                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
            
            
                  and yonghu.new_money >= #{params.newMoneyStart}   
            
            
                  and yonghu.new_money <= #{params.newMoneyEnd}   
            
            
                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   
            
            
                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   
            
            
                and yonghu.yonghu_delete = #{params.yonghuDelete}
            
                
            
                and a.zuowei_id IS NOT NULL
            
            
                and zuowei.zuowei_name like CONCAT('%',#{params.zuoweiName},'%')
            
            
                and zuowei.zuowei_types = #{params.zuoweiTypes}
            

            
                and zuowei.zuowei_shijian like CONCAT('%',#{params.zuoweiShijian},'%')
            
            
                  and zuowei.zuowei_new_money >= #{params.zuoweiNewMoneyStart}   
            
            
                  and zuowei.zuowei_new_money <= #{params.zuoweiNewMoneyEnd}   
            
            
                  and zuowei.zuowei_number >= #{params.zuoweiNumberStart}   
            
            
                  and zuowei.zuowei_number <= #{params.zuoweiNumberEnd}   
            
            
                and zuowei.zuowei_number = #{params.zuoweiNumber}
            
            
                  and zuowei.zuowei_clicknum >= #{params.zuoweiClicknumStart}   
            
            
                  and zuowei.zuowei_clicknum <= #{params.zuoweiClicknumEnd}   
            
            
                and zuowei.zuowei_clicknum = #{params.zuoweiClicknum}
            
            
                  and zuowei.zuowei_delete >= #{params.zuoweiDeleteStart}   
            
            
                  and zuowei.zuowei_delete <= #{params.zuoweiDeleteEnd}   
            
            
                and zuowei.zuowei_delete = #{params.zuoweiDelete}
            
            
                and zuowei.zuowei_content like CONCAT('%',#{params.zuoweiContent},'%')
            
        

        order by a.${params.orderBy} desc
